export function debounce(delay = 300) {
  let interval;
  return function (target, propertyKey, descriptor) {
    const original = descriptor.value;

    descriptor.value = function (...args) {
      clearTimeout(interval);
      interval = setTimeout(() => {
        interval = null;
        original.apply(this, args);
      }, delay);
    };

    return interval;
  };
}
